import React, { useCallback, useState, useEffect } from 'react';
import { Form, Input, Button } from 'antd';
import { useSelector, useDispatch } from 'react-redux';
import { ADD_POST_REQUEST } from '../reducers/post';

const PostForm = () => {
    const dispatch = useDispatch();
    const [text, setText] = useState('');
    const { imagePaths, isAddingPost } = useSelector(state => state.post);

    const onSubmitForm = useCallback(() => {
        dispatch({
            type: ADD_POST_REQUEST,
            data: {
                text,
            }
        });
    }, [])

    const onChangeText = useCallback((e) => {
        setText(e.target.value);
    }, [])

    return (
        <Form encType="multipart/form-data" onSubmit={onSubmitForm}>
            <Input.TextArea maxLength={140} placeholder="일상을 적어주세요." value={text} onChange={onChangeText} />
            <div>
                <input type="file" multiple hidden />
                <Button>이미지 업로드</Button>
                <Button type="primary" htmlType="submit" loading={isAddingPost}>짹짹</Button>
            </div>
            <div>
                {imagePaths.map((v, i) => {
                    return (
                        <div key={v} style={{ display: 'inline-block' }}>
                            <img src={`http://localhost:3000/${v}`} style={{ width: '200px' }} alt={v} />
                            <div>
                                <Button>제거</Button>
                            </div>
                        </div>
                    )
                })}
            </div>
        </Form>
    );
};

export default PostForm;
