export const LOG_IN = 'LOG_IN'; 
export const LOG_OUT = 'LOG_OUT'; 

export const loginAction = {
    type: LOG_IN,
    data: {
        nickname: 'jason'
    }
};

export const logoutAction = {
    type: LOG_OUT
};

export const initialState = {
    isLoggedIn: false,
    user: {},
};

const reducer = (state=initialState, action) => {
    switch(action.type){
        case LOG_IN: {
            return {
                ...state,
                isLoggedIn: true,
                user: action.data
            }
        }
        case LOG_OUT: {
            return {
                ...state,
                isLoggedIn: false,
                user: null
            }
        }
    }
};

export default reducer;
