import React, { useState, useCallback, useEffect } from 'react';
import Link from 'next/link';
import { Card, Icon, Button, Avatar, List, Form, Input, Comment, Popover } from 'antd';
import PropTypes from 'prop-types';
import { useSelector, useDispatch } from 'react-redux';
import { ADD_COMMENT_REQUEST, LOAD_COMMENTS_REQUEST, UNLIKE_POST_REQUEST, LIKE_POST_REQUEST, RETWEET_REQUEST, REMOVE_POST_REQUEST } from '../reducers/post';
import { FOLLOW_USER_REQUEST, UNFOLLOW_USER_REQUEST } from '../reducers/user';
import PostImages from './PostImages';
import PostCardContent from './PostCardContent';

const PostCard = ({ post }) => {
    const [commentFormOpened, setCommentFormOpened] = useState(false);
    const [commentText, setCommentText] = useState(''); 
    const { me } = useSelector(state => state.user);
    const { commentAdded, isAddingComment } = useSelector(state => state.post);
    const dispatch = useDispatch();

    const liked = me && post.Likers && post.Likers.find(v => v.id === me.id);

    const onSubmitComment = useCallback((e) => {
        e.preventDefault();
        if( !me ){
            return alert('댓글 입력을 위해서는 로그인을 해주세요.');
        }
        return dispatch({
            type: ADD_COMMENT_REQUEST,
            data: {
                postId: post.id,
                content: commentText,
            }
        })
    }, [me && me.id, commentText]);

    const onChangeCommentText = useCallback((e) => {
        const { value } = e.target;
        setCommentText(value);
    }, []);

    const onToggleComment = useCallback(() => {
        setCommentFormOpened(prev => !prev);
        if( !commentFormOpened ){
            dispatch({
                type: LOAD_COMMENTS_REQUEST,
                data: post.id,
            })
        }
    }, []);

    const onToggleLike = useCallback(() => {
        if( !me ){
            return alert('로그인이 필요합니다.');
        }

        if( liked ){ // 좋아요 누른 상태
            dispatch({
                type: UNLIKE_POST_REQUEST,
                data: post.id,
            })
        } else { // 좋아요 안 누른 상태
            dispatch({
                type: LIKE_POST_REQUEST,
                data: post.id,
            })
        }
    }, [me && me.id, post && post.id, liked]);

    const onRetweet = useCallback(() => {
        if( !me ){
            return alert('로그인이 필요합니다.');
        }
        dispatch({
            type: RETWEET_REQUEST,
            data: post.id,
        })
    }, [me && me.id, post.id]);

    const onFollow = useCallback(userId => () => {
        dispatch({
            type: FOLLOW_USER_REQUEST,
            data: userId,
        })
    }, []);

    const onUnfollow = useCallback(userId => () => {
        dispatch({
            type: UNFOLLOW_USER_REQUEST,
            data: userId,
        })
    }, []);

    const onRemovePost = useCallback(postId => () => {
        dispatch({
            type: REMOVE_POST_REQUEST,
            data: postId,
        })
    }, [post.id]);

    useEffect(() => {
        setCommentText('');
    }, [commentAdded === true])

    return (
        <div>
            <Card
                key={+post.createdAt}
                cover={post.Images && post.Images[0] && <PostImages images={post.Images} />}
                actions={[
                    <Icon type="retweet" key="retweet" onClick={onRetweet} />,
                    <Icon type="heart" key="heart" theme={ liked ? "twoTone" : "outlined" } twoToneColor="#eb2f96" onClick={onToggleLike} />,
                    <Icon type="message" key="message" onClick={onToggleComment} />,
                    <Popover
                        key="ellipsis"
                        content={(
                            <Button.Group>
                                {me && post.UserId === me.id
                                    ? ( 
                                        <>
                                            <Button>수정</Button>
                                            <Button type="danger" onClick={onRemovePost(post.id)}>삭제</Button>
                                        </>
                                    )
                                    : <Button>신고</Button>
                                }
                            </Button.Group>
                        )}
                    >
                        <Icon type="ellipsis" />
                    </Popover>,
                ]}
                title={post.RetweetId ? `${post.User.nickname}님이 리트윗 하셨습니다.` : null}
                extra={ !me || post.User.id === me.id
                    ? null
                    : me.Followings && me.Followings.find(v => v.id === post.User.id)
                        ? <Button onClick={onUnfollow(post.User.id)}>언팔로우</Button>
                        : <Button onClick={onFollow(post.User.id)}>팔로우</Button>
                }
            >
                { post.RetweetId && post.Retweet 
                ?   <Card
                        cover={ post.Retweet.Images[0] && <PostImages images={post.Retweet.Images} /> }
                    >
                        <Card.Meta 
                            avatar={(
                                <Link href={{ pathname: '/user', query: { id: post.Retweet.User.id } }} as={`/user/${post.Retweet.User.id}`}>
                                    <a>
                                        <Avatar>{post.Retweet.User.nickname[0]}</Avatar>
                                    </a>
                                </Link>
                            )}
                            title={post.Retweet.User.nickname}
                            description={ <PostCardContent postData={post.Retweet.content}/> }
                        />
                    </Card>
                :   (<Card.Meta 
                        avatar={(
                            <Link href={{ pathname: '/user', query: { id: post.User.id } }} as={`/user/${post.User.id}`}>
                                <a>
                                    <Avatar>{post.User.nickname[0]}</Avatar>
                                </a>
                            </Link>
                        )}
                        title={post.User.nickname}
                        description={ <PostCardContent postData={post.content}/> }
                    />
                )}
            </Card>
            {commentFormOpened && (
            <>
                <Form onSubmit={onSubmitComment}>
                    <Form.Item>
                        <Input.TextArea row={4} value={commentText} onChange={onChangeCommentText} />
                    </Form.Item>
                    <Button type="primary" htmlType="submit" loading={isAddingComment}>삐약</Button>
                </Form>
                <List 
                    header={`${post.Comments ? post.Comments.length : 0}개의 댓글`}
                    itemLayout="horizontal"
                    dataSource={post.Comments || []}
                    renderItem={item => (
                        <li>
                            <Comment 
                                author={item.User.nickname}
                                avatar={(
                                    <Link href={{ pathname: '/user', query: { id: item.User.id } }} as={`/user/${item.User.id}`}>
                                        <a>
                                            <Avatar>{item.User.nickname[0]}</Avatar>
                                        </a>
                                    </Link>
                                )}
                                content={item.content}
                            />
                        </li>
                    )}
                />
            </>
            )}
        </div>
    );
};

PostCard.propTypes = {
    post: PropTypes.shape({
        User: PropTypes.object,
        content: PropTypes.string,
        img: PropTypes.string,
        // createdAt: PropTypes.object,
    }).isRequired,
};

export default PostCard;
