export const initialState = {
    mainPosts: [{
        User: {
            id: 1,
            nickname: 'jason',
        },
        content: '첫 게시글',
        img: '',
    }],
    imagePath: [],
};

const ADD_POST = 'ADD_POST';
const ADD_DUMMY = 'ADD_DUMMY';

const addPost = {
    type: ADD_POST
};

const addDummy = {
    type: ADD_DUMMY,
    data: {
        content: 'Hi!!!!!!!!!',
        UserId: 1,
        User: {
            nickname: 'jason',
        }
    }
};

const reducer = (state=initialState, action) => {
    switch(action.type){
        case ADD_POST: {
            return {
                ...state,
            }
        }
        case ADD_DUMMY: {
            return {
                ...state,
                mainPosts: [action.data, ...state.mainPosts]
            };
        }
        default: {
            return {
                ...state,
            }
        }
    }
};

export default reducer;
