import React, { useCallback } from 'react';
import { Card, Avatar, Button } from 'antd';
import { useSelector, useDispatch } from 'react-redux';
import { logoutAction } from '../reducers/user';

const UserProfile = () => {
    const { user } = useSelector(state => state.user);
    const dispatch = useDispatch();

    const onLogout = useCallback((e) => {
        e.preventDefault();
        dispatch(logoutAction);    
    }, []);

    return (
        <Card
            actions={[
                <div key="twit">트윗 <br/> {user.Post.length}</div>,
                <div key="followings">팔로잉 <br/> {user.Followings.length}</div>,
                <div key="followers">팔로워 <br/> {user.Followers.length}</div>
            ]}
        >
            <Card.Meta
                avatar={<Avatar>{user.nickname}</Avatar>} 
                title={user.nickname}
            />
            <Button onCilck={onLogout}>로그아웃</Button>
        </Card>
    );
};

export default UserProfile;
